() => {
  const appearance = select(
    'appearance',
    ['primary', 'secondary', 'alert', 'warning', 'success', 'accent1', 'accent2', 'accent3', 'accent4'],
    undefined
  );

  const size = select('size', ['regular', 'tiny'], undefined);
  const withTooltip = boolean('with tooltip', true);

  const children = text('children', '');
  const firstName = text('firstName', 'John');
  const lastName = text('lastName', 'Doe');

  const options = {
    appearance,
    withTooltip,
    size,
    firstName,
    lastName,
  };

  return <Avatar {...options}>{children}</Avatar>;
}

() => {
  const weight = 'strong';

  const appearances: Appearance[] = [
    'primary',
    'secondary',
    'alert',
    'warning',
    'success',
    'accent1',
    'accent2',
    'accent3',
    'accent4',
  ];

  return (
    <div className="d-flex">
      {appearances.map((appear, ind) => {
        return (
          <div key={ind} className="mr-9">
            <Text weight={weight}>{appear.charAt(0).toUpperCase() + appear.slice(1)}</Text>
            <br />
            <br />
            <Avatar firstName="John" lastName="Doe" appearance={appear} />
          </div>
        );
      })}
    </div>
  );
}

() => {
  const weight = 'strong';

  return (
    <div className="d-flex">
      <div className="mr-9 d-flex flex-column">
        <Text weight={weight}>Regular</Text> <br />
        <Avatar firstName="John" lastName="Doe" />
      </div>
      <div className="mr-9  d-flex flex-column">
        <Text weight={weight}>Tiny</Text> <br />
        <Avatar firstName="John" lastName="Doe" size="tiny" />
      </div>
    </div>
  );
}

() => {
  const position = select(
    'position',
    ['top', 'top-start', 'top-end', 'bottom', 'bottom-start', 'bottom-end'],
    'bottom'
  );

  const on = select('on', ['click', 'hover'], 'hover');

  const dark = boolean('dark', true);

  const max = number('max', 2);

  const options = {
    max,
    popoverOptions: {
      on,
      position,
      dark,
    },
    list: list.slice(0, 4),
  };

  return <AvatarGroup {...options} />;
}

