() => {
  const data = [
    {
      firstName: 'John',
      lastName: 'Doe',
      email: 'jonathandoe@gamil.com',
      owner: true,
    },
    {
      firstName: 'Katty',
      lastName: 'Perry',
      email: 'kattyperry21@gamil.com',
      edit: true,
    },
    {
      firstName: 'Daniel',
      lastName: 'Low',
      email: 'daniellow02@yahoo.com',
      view: true,
    },
  ];

  const schema: TableProps['schema'] = [
    {
      name: 'info',
      displayName: 'Info',
      width: '80%',
      cellType: 'AVATAR_WITH_META_LIST',
      translate: (a) => ({
        firstName: a.firstName,
        lastName: a.lastName,
        title: `${a.firstName} ${a.lastName}`,
        metaList: [a.email],
      }),
    },
    {
      name: 'rights',
      displayName: 'Rights',
      width: '20%',
      cellRenderer: (props: GridCellProps) => {
        const renderRights = () => {
          if (props.data.owner) {
            return (
              <Text appearance="subtle" className="pr-5">
                owner
              </Text>
            );
          }

          if (props.data.edit || props.data.view) {
            const rights = props.data.edit ? 'edit' : 'view';
            return (
              <Button
                icon="keyboard_arrow_down"
                iconAlign="right"
                appearance="transparent"
                onClick={(e) => e.stopPropagation()}
              >
                {`can ${rights}`}
              </Button>
            );
          }

          return null;
        };

        return <div className="d-flex align-items-center justify-content-end flex-grow-1">{renderRights()}</div>;
      },
    },
  ];

  return (
    <Card className="py-4">
      <Text size="large" weight="strong" className="ml-5">
        Sharing Test Manual
      </Text>
      <List
        type="resource"
        withHeader={true}
        headerOptions={{
          withSearch: true,
          dynamicColumn: false,
        }}
        separator={false}
        showMenu={false}
        data={data}
        schema={schema}
        withPagination={false}
        onSearch={(currData, searchTerm) => {
          return currData.filter(
            (d) =>
              d.firstName.toLowerCase().match(searchTerm.toLowerCase()) ||
              d.lastName.toLowerCase().match(searchTerm.toLowerCase())
          );
        }}
        onRowClick={(rowData, rowIndex) =>
          action(`on-row-click:- rowIndex: ${rowIndex} data: ${JSON.stringify(rowData)}`)()
        }
      />
    </Card>
  );
}

