() => {
  const loading = boolean('loading', true);

  const error = boolean('error', false);

  const pageSize = number('pageSize', 5);

  const withCheckbox = boolean('withCheckbox', true);

  const applyLoaderSchema = boolean('applyLoaderSchema', true);

  const applySchema = boolean('applySchema', false);

  const applyData = boolean('applyData', false);

  return (
    <div
      style={{
        height: '350px',
        // overflow: 'hidden'
      }}
    >
      <Card className="h-100 overflow-hidden">
        <Table
          loading={loading}
          error={error}
          pageSize={pageSize}
          withCheckbox={withCheckbox}
          loaderSchema={applyLoaderSchema ? loaderSchema : []}
          data={applyData ? data : []}
          schema={applySchema ? schema : []}
          showMenu={true}
          withHeader={true}
          headerOptions={{
            withSearch: true,
          }}
        />
      </Card>
    </div>
  );
}

